mapper.update:
update messages
set receiveDelete=1
where mid=#{mid}

update messages
set sendDelete=1
where mid=#{mid}

update messages
set readDate=now()
where mid=#{mid}

mapper.select:
select m.*, u.uname, u.photo
from messages m, users u
where m.receiver=u.uid and mid=#{mid};

select m.*, u.uname, u.photo
from messages m, users u
where m.sender=u.uid and mid=#{mid};
