mapper.update:
update students
set sname=#{sname}, year=#{year}, birthday=#{birthday}, advisor=#{advisor}
where scode=#{scode}

mapper.select:
 	select * from view_stu

 		where dept like #{word}


 		where scode like #{word}


 		where sname like #{word}


 		where pname like #{word}

		order by scode desc
		limit #{start}, #{size};

select count(*)
from view_stu

where dept like #{word}


where scode like #{word}


where sname like #{word}


where pname like #{word}
