mapper.select:
select r.*, title,image, price, format(price,0) fmtprice
from goods g inner join related r on r.rid=g.gid
where r.gid=#{gid};

select count(*)
from related
where gid=#{gid} and rid=#{rid}

mapper.update:
update goods
set contents=#{contents}
where gid=#{gid}

update goods
set image=#{image}
where gid=#{gid}

update goods
set title=#{title}, price=#{price}, maker=#{maker}, brand=#{brand}
where gid=#{gid}
